'use strict';

module.exports = function(Meanio) {
  var DATABASE = process.env.DATABASE || 'mongo';
  switch (DATABASE) {
    case 'mongo':
      return require('./mongo.js')(Meanio);
    case 'cassandra':
      return require('./cassandra.js')(Meanio);
    case 'postgres':
      return require('./postgres.js')(Meanio);
    case 'sqlite3':
      return require('./sqlite.js')(Meanio);
  }
};
